         <hr/>

          <dl className ='row'>
            <dl className="col-sm-3">Description</dl>
            <dl className="col-sm-9">{description}</dl>

            <dl className="col-sm-3">Post category</dl>
            <dl className="col-sm-9">{postCategory}</dl>
          </dl>


      </div>
    )
  }
}
