//class components
import React, { Component } from 'react';
import axios from 'axios';



export default class Home extends Component {

constructor(props){
  super(props);

  this.state={
     posts:[]
  };
}


//components and subcomp render after
componentDidMount(){
  this.retrivePosts();
}

retrivePost(){
  axios.get("/posts").then(res =>{
    if(res.data.sucess){
      this.setState({
        post:res.data.existingPost
      });

      console.log(this.state.post)
    }
  });
}
  render(){
    return(
      <div className="container">
        <h1>All post</h1>
          
          <table class ="table">
            <thead>
              <tr>
              <th scope = "col">#</th>
              <th scope = "col">Topic</th>
              <th scope = "col">Description</th>
              <th scope = "col">Post category</th>
              <th scope = "col">Action</th>
              </tr>
            </thead>
            <tbody>
           
                {this.state.post.map((post,index)=>(
                  <tr key={index}>
                    <th scope="row">{index+1}</th>
                    <td>
                        <a href ={`/post/ ${ post._id }`} style={{textDecoration:"none"}}>
                        {post.topic}
                        </a>
                            
                    </td>
                    <td>{post.description}</td>
                    <td>{post.postCategory}</td>
                    <td>
                        <a classsName="btn btn-warning" href={`"/edit/${post._id}`}>
                          <i className="fas fa-edit"></i>&nbsp;Edit
                        </a>
                        &nbsp;
                        <a classsName="btn btn-danger" href="#">
                          <i className="fas fa-trash-alt"></i>&nbsp;Delete
                        </a>
                    </td>
                  </tr>
              ))
              }
            </tbody>
          </table>


          <button className="btn btn-sucess"><a href="/add"style={{textDecoration:"none",color:"white"}}>Create Post</a></button>
          

      </div>
    )
  }
}
